hex_string = ""



def new_decoder(hex_string):
    decoded_data = []
    try:
        # from Token
        from_token = hex(int(hex_string[34:74], 16))
        decoded_data.append({"Name": "fromToken", "Type": "address", "Data": from_token})

        # to Token
        to_token = hex(int(hex_string[98:138], 16))
        decoded_data.append({"Name": "toToken", "Type": "address", "Data": to_token})
        # fromAmount
        from_amount = int(hex_string[138:202], 16)
        decoded_data.append({"Name": "fromAmount", "Type": "uint256", "Data": from_amount})
        # recipient
        recipient = hex(int(hex_string[226:266], 16))
        decoded_data.append({"Name": "recipient", "Type": "address", "Data": recipient})
        # minReturn
        min_return = int(hex_string[266:330], 16)
        decoded_data.append({"Name": "minReturn", "Type": "uint256", "Data": min_return})
        # permission
        permission = hex(int(hex_string[522:652], 16))
        decoded_data.append({"Name": "permission", "Type": "bytes", "Data": permission})

    except Exception as e:
        return e
    return decoded_data


data = new_decoder(hex_string)
print(data)





